    // },
    // resetResponseMsg: (state) => {
    //   state.isLoading = false;
    //   state.replyTicketError = "";
    //   state.replyMsg = "";
    // },
  },
});

const { reducer, actions } = ticketListSlice;

export const {
  fetchTicketLoading,
  fetchTicketSuccess,
  fetchTicketFail,
searchTickets,
fetchSingleTicketFail,
fetchSingleTicketLoading,
fetchSingleTicketSuccess,
} = actions;

export default reducer;
