import React, { useEffect, useState } from 'react'



import { Form,Button } from 'react-bootstrap'
import { useDispatch,useSelector } from 'react-redux'
import {replyOnTicket} from '../../page/ticket-listing/TicketAction'


export const TicketUpdate = (id) => {
   const [message,setmessage]= useState('')
   const{ user:{name}}=useSelector(state=>state.user)
   const dispatch=useDispatch()
   const handlOnchange =e=>{

      setmessage(e.target.value)
      console.log(message)

   }
   const handlOnsubmit =(e)=>{
      e.preventDefault()
      const msgObj={

      message,
      sender:name

     }
      dispatch(replyOnTicket(id,msgObj))

   }

      return (

         <Form onSubmit={handlOnsubmit}>

         <Form.Text>please do it</Form.Text>
         <Form.Control

         as="textarea"
         name=""
         value={message}
         onChange={handlOnchange}
         row="5"

         />

         <div className="text-right mt-3 mb-3">
         <Button varient="info" type="submit">  submit</Button>
        </div>

     </Form>


  )
}
